import React, { Component, Fragment } from 'react';
import { Button, message, Avatar, Row, Col } from 'antd';
import { Link, Redirect } from 'react-router-dom';
import store from "../store";
class AccountBar extends Component {
    state={
        fromPath:this.props.path,
        redirect: false
    }

    handleRoute = () =>{
        this.setState({
            redirect:true
        })
        console.log(this.state)
    }

    render(){
        const fromPath = this.state.fromPath;
        const redirect = this.state.redirect;
        if(redirect){
            return <Redirect to="/userPage"/>;
        }
        return (
            <div>
                <Row style={{height:150}}>
                    <Col span={5}>
                    <Link to={{pathname:{fromPath}}}>
                        <Button icon="home" type="link" onClick={()=>this.handleRoute()}></Button>
                    </Link>
                    </Col>
                </Row>
            </div> 
        );
    }
}

export default AccountBar;
