import React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Input,
  Button,
  message,
  Icon,
  Checkbox,
  notification,
  Statistic,
  Avatar,
  Divider,
  Progress,
  Slider,
  InputNumber,
  Menu
} from "antd";
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { Redirect, Link } from "react-router-dom";
import AccountBar from './AccountBar'
import UserAvartar from '../PIC/u50.svg'

//const { Meta } = Card;
const {Countdown} = Statistic;
const deadline = Date.now() + 1000 * 60 * 60 * 24 * 2 + 1000 * 30;
const marks = {
    0: '0°C',
    26: '26°C',
    36: '36°C',
    36: {  style: { color: '#f50',},label: <strong>36°C</strong>,}
}

class UserPage extends React.Component {
    constructor(){
        super()

        this.state={
            temprature:26,
            windForce:0,
            workingMode:0,
            windText:"关机",
            roomId:301,
            startTime:new Date(),
            time:new Date(),
        }
        setInterval(function(){
            this.setState({
                time:new Date()
            })
        }.bind(this), 1000)
    }

    onFinishCount() {
        console.log('finished')
    }

    onTemUpOne = () => {
        var tem = this.state.temprature + 1
        this.setState({
            temprature: tem
        })
    }

    onTemLowOne = () =>{
        var tem = this.state.temprature - 1
        this.setState({
            temprature: tem
        })
    }

    ChangeWorkingMode = () =>{
        var tem = (this.state.workingMode + 1) % 2
        this.setState({
            workingMode: tem
        })
    }

    ChangeWindForce = () =>{
        var tem = (this.state.windForce + 1) % 4
        this.setState({
            windForce: tem
        })
    }

    onTemChange = value =>{
        this.setState({
            temprature:value
        })
    }

    render(){
        const {temprature} = this.state;
        if(this.state.windForce === 0){
            var windForce = 0;
            var windText = "关机"
        }else if(this.state.windForce === 1){
            var windForce = 33;
            var windText = "低速"
        }else if(this.state.windForce === 2){
            var windForce = 66;
            var windText = "中速"
        }else if(this.state.windForce === 3){
            var windForce = 100;
            var windText = "高速"
        }

        if(this.state.workingMode === 0){
            var workingMode = "制冷";
        }else if(this.state.workingMode === 1){
            var workingMode = "制热";
        }
        const {roomId, startTime, time} = this.state

        return(
            <div>
            <div style={{width:"100%", backgroundColor:"#000000"}}>
                <Row style={{height:75}}>
