const defaultState = {
    user:0,
    UserName:"default",
    roomItem:{},
    roomList:[
    ],
    lowTem:18,
    highTem:25,
}
export default (state = defaultState, action) => {
    if(action.type === "Set"){
        const newState = JSON.parse(JSON.stringify(state));
        newState.roomList = action.roomList;
        return newState;    
    }

    return state;
}
