import React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Input,
  Button,
  message,
  Icon,
  Checkbox,
  notification,
  Statistic,
  Avatar,
  Divider,
  Progress
} from "antd";
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { Redirect, Link } from "react-router-dom";

const { Meta } = Card;
const {Countdown} = Statistic;
const deadline = Date.now() + 1000 * 60 * 60 * 24 * 2 + 1000 * 30;

class UserPage extends React.Component {
    state={
        temprature:26,
        windForce:0,
        workingMode:0,
        windText:"关机"
    }

    onFinishCount() {
        console.log('finished')
    }

    onTemUpOne = () => {
        var tem = this.state.temprature + 1
        this.setState({
            temprature: tem
        })
    }

    onTemLowOne = () =>{
        var tem = this.state.temprature - 1
        this.setState({
            temprature: tem
        })
    }

    ChangeWorkingMode = () =>{
        var tem = (this.state.workingMode + 1) % 2
        this.setState({
            workingMode: tem
        })
    }

    render(){
        const temprature = this.state.temprature;
        if(this.state.windForce === 0){
            var windForce = 0;
            var windText = "exception"
        }else{
            var windText = "active"
            if(this.state.windForce === 1){
                var windForce = 33;
            }else if(this.state.windForce === 2){
                var windForce = 66;
            }else if(this.state.windForce === 3){
                var windForce = 100;
            }
        }

        if(this.state.workingMode === 0){
            var workingMode = "制冷";
        }else if(this.state.workingMode === 1){
            var workingMode = "制热";
        }
        return(
            <div>
            <Row style={{height:100}}>

            </Row>
            <Row style={{height:100}}>
                <Col span={5}/>
                <Col span={12}>
                    <Countdown title="等待中" value={deadline} onFinish={this.onFinishCount} />
                </Col>
            </Row>
            <Row style={{height:500}}>
                <Col span={5}/>
                <Col span={12}>
                <Card
                    style={{ width: 300 }}
                    cover={
                    <img
                        alt="example"
                        src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                    />
                    }
                    actions={[
                    <SettingOutlined key="setting" />,
                    <EditOutlined key="edit" />,
                    <EllipsisOutlined key="ellipsis" />,
                    ]}
                    >
                    <Meta
                    style={{height: 50}}
                    avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                    title="我的空调遥控"
                    //description={temprature}
                    > 
                    </Meta>
                    <Card>
                    <Progress type="circle" percent={windForce} format={windText => `${windText}`} />
                        <div style={{textAlign:"center"}}>
                            {workingMode}
                            <Divider type="vertical" style={{height:30}}/>    
                            {temprature} ℃
                        </div>
                        <Progress percent={windForce} status={windText} />
                    </Card>
                    <div style={{marginTop:10, textAlign:"center"}}>
                        <Button style={{marginRight:10}}  onClick={this.ChangeWorkingMode}>模式</Button>
                        <Button style={{marginRight:10}}  onClick={this.onTemUpOne}>风速</Button>
                        <Button style={{marginRight:10}} type="primary" shape="circle" onClick={this.onTemUpOne}>+</Button>
                        <Button style={{marginLeft:10}} type="primary" shape="circle" onClick={this.onTemLowOne}>-</Button>
                    </div>
                </Card>
                </Col>
            </Row>
            </div>
        )
    }

}

export default UserPage;
