import React, { Fragment}from "react";
import { Button,Input, Tooltip, Col, Row, Menu, Dropdown, Icon, Table,Divider, Card} from 'antd';

const InputGroup = Input.Group;

class RecepPage extends React.Component {
    constructor(props){
        super(props)

        this.columns = [
            {
              title: '房间',
              dataIndex: 'roomId',
              width: '30%',
            },
            {
              title: '开始时间',
              dataIndex: 'beginTime',
            },{
                title: '结束时间',
                dataIndex: 'endTime',
            },{
                title: '送风时间/秒',
                dataIndex: 'windTime',
            },{
                title: '风速',
                dataIndex: 'eSpeed',
            },{
                title: '费率',
                dataIndex: 'price',
            },{
                title: '费用',
                dataIndex: 'fee',
            },
        ]

        this.state={
            inTime:"--",
            outTime:"--",
            totalFee:0,
            data:[
            ]
        }

    }

    handleSelect(value){
        this.setState({from:value});
    }

    handleIn(){
        const room = this.state.from
        if(room == 101){
            this.setState({
                data:[
                    {
                        "beginTime":"2020-06-27 22:41:28",
                        "eSpeed":0.5,
                        "endTime":"2020-06-27 22:42:28",
                        "fee":0.5,
                        "price":1,
                        "roomId":101,
                        "windTime":60
                    },
                    {
                        "beginTime":"2020-06-27 22:42:28",
                        "eSpeed":0.5,
                        "endTime":"2020-06-27 22:43:57",
                        "fee":0.5,
                        "price":1,
                        "roomId":101,
                        "windTime":60
                    },
                    {
                        "beginTime":"2020-06-27 22:44:27",
                        "eSpeed":0.5,
                        "endTime":"2020-06-27 22:45:27",
                        "fee":0.5,
                        "price":1,
                        "roomId":101,
                        "windTime":60
                    },
                    {
                        "beginTime":"2020-06-27 22:46:28",
                        "eSpeed":1.0,
                        "endTime":"2020-06-27 22:50:28",
                        "fee":4.0,
                        "price":1,
                        "roomId":101,
                        "windTime":240
                    },
                    {
                        "beginTime":"2020-06-27 22:50:28",
                        "eSpeed":1.0,
                        "endTime":"2020-06-27 22:55:28",
                        "fee":5.0,
                        "price":1,
                        "roomId":101,
                        "windTime":300
                    },
                    {
                        "beginTime":"2020-06-27 23:03:29",
                        "eSpeed":1.0,
                        "endTime":"2020-06-27 23:05:29",
                        "fee":1.0,
                        "price":1,
                        "eSpeed":1,
                        "fee":1,
                        "roomId":101,
                    }],
                inTime:"27/6/2020 22:40:28",
                outTime:"27/6/2020 23:05:29",
                totalFee:11.5
            })
        }else if(room == 102){
            this.setState({
                data:[],
                inTime:"27/6/2020 22:40:28",
                outTime:"27/6/2020 23:06:29",
                totalFee:5.04
            })
        }else if(room == 103){
            this.setState({
                data:[],
                inTime:"27/6/2020 22:40:28",
                outTime:"27/6/2020 23:03:29",
                totalFee:7
            })
        }else if(room == 104){
            this.setState({
                data:[],
                inTime:"27/6/2020 22:40:28",
                outTime:"27/6/2020 23:06:29",
                totalFee:10.28
            })
        }else if(room == 105){
            this.setState({
                data:[],
                inTime:"27/6/2020 22:40:28",
                outTime:"27/6/2020 23:04:29",
                totalFee:15.01
            })
        }else{
            this.setState({
                data:[],
                inTime:"--",
                outTime:"--",
                totalFee:0
            })
        }
    }

    handleFrom(event){
        let value = event.target.value;
        this.setState({from:value})
    }

    OnMenu(key){
        this.setState({
            from:key.item.props.children
        })
    }

    render(){
        const menu = (
            <Menu onClick={(key)=>this.OnMenu(key)}>
              <Menu.Item key="0">101</Menu.Item>
              <Menu.Item key="1">102</Menu.Item>
              <Menu.Item key="3">103</Menu.Item>
              <Menu.Item key="4">104</Menu.Item>
              <Menu.Divider />
              <Menu.Item key="5">这里是一些例子</Menu.Item>
            </Menu>
        );
        const columns = this.columns
        const {data, inTime, outTime, totalFee} = this.state
        return(

            <div>
            <div style={{width:"100%", backgroundColor:"#000000"}}>
                <Row style={{height:75}}>
                <Icon style={{margin:"15px",fontSize: '35px',color:"white"}} type="menu-fold" />
                <span style={{margin:"15px",fontSize: '35px',color:"white"}}>温控计费系统</span>
                <Icon style={{margin:"15px",fontSize: '35px',color:"#87CEEB"}} type="code-sandbox" />
                <span style={{margin:"15px",fontSize: '35px',color:"white"}}>前台端</span>
                </Row>
            </div>
            <Row style={{height:500}}>
                <Col span={4}>
                <Menu
                defaultSelectedKeys={['1']}
                defaultOpenKeys={['sub1']}
                mode="inline"
                theme="dark"
                inlineCollapsed={this.state.collapsed}
                style={{height:1000}}
                >
                    <Menu.Item key="1">
                        <Icon type="pie-chart" />
                        <span style={{fontSize:17}}>账单打印</span>
                    </Menu.Item>
                    <Menu.Item key="2">
                        <Icon type="desktop" />
                        <span style={{fontSize:17}}>使用说明</span>
                    </Menu.Item>
                    <Menu.Item key="3">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>联系运营</span>
                    </Menu.Item>
                    <Menu.Item key="4">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>捐赠支持</span>
                    </Menu.Item>
                    <Menu.Item key="5">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>退出登录</span>
                    </Menu.Item>
                </Menu>
                </Col>

                <Col>
                <Row style={{height:20}}/>
                <Col span={1}/>
                <Col span={15}>
                <Fragment>
                <InputGroup compact onSearch={value => console.log(value)}>
                    <Tooltip title="输入查询用户的房间">
                    <div >
                    <Dropdown overlay={menu} trigger={['click']}>
                        <Input
                        style={{ width: 180, textAlign: 'center' }}
                        placeholder="请输入查询房间"
                        value = {this.state.from}
                        onChange={event => this.handleFrom(event)}
                        allowClear = {true}
                        />
                    </Dropdown>
                    </div>
                    </Tooltip>
                    <Button type="primary" onClick={()=>{this.handleIn()}}>打印详单</Button>
                </InputGroup>
                </Fragment>
                <Divider/>
                <Card 
                    style={{marginTop:30, width:1050, height:700}}
                >
                <h2>账单结果一览</h2>
                <Divider/>
                <Table columns={columns} dataSource={data} 
                    bordered
                />
                <span style={{marginRight:50, fontSize:18}}>入住时间:{inTime}</span>
                <span style={{marginRight:50, fontSize:25}}>出房时间:{outTime}</span>
                <span style={{marginRight:50, fontSize:25}}>总费用:{totalFee}</span>
                </Card>
                </Col>
                </Col>
            </Row>
            </div>
        )
    }

}

export default RecepPage;
