import React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Input,
  Button,
  TimePicker,
} from "antd";

const { RangePicker } = TimePicker;

class AdministratorPage extends React.Component {
    state={
        city:null,
        startTime:null,
        endTime:null,
        numberOfPeople:null,
        date:[]
    }

    onChangeRange = (value1, value2) =>{
        console.log(value2);
        //增加date用于记录标准日期，且只有有日期时才进行标准化
        var date = [null, null];
        if((value2[0] !== null && value2[1] !== null) &&
        (value2[0] !== "" && value2[1] !== "")){
        date = [moment(value2[0], dateFormat), moment(value2[1], dateFormat)];
        }
        this.setState({
        startTime:value2[0], 
        endTime:value2[1],
        date:date
        });
    }

    render(){
        return(
            <div>
                <RangePicker
                    style={{ width: 300 }}
                    size="large"
                    placeholder={["入住日期", "退房日期"]}
                    value={this.state.date}
                    onChange = {(value1,value2)=>this.onChangeRange(value1,value2)}
                />
            </div>
        )
    }

}

export default AdministratorPage;
