import React from "react";
import {
  Row,
  Col,
  Card,
  Form,
  Input,
  Button,
  message,

  Checkbox,
  notification
} from "antd";
import { Redirect, Link } from "react-router-dom";
import store from "../store";

const FormItem = Form.Item;
class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      username: "",
      password: "",
      remind: "",
      rememberPassword: false,
      fromPath: "/"
    };
  }

  loadAccountInfo = () => {
    let arr,
      reg = new RegExp("(^| )" + "accountInfo" + "=([^;]*)(;|$)");
    let accountInfo = "";
    arr = document.cookie.match(reg);
    if (arr) {
      accountInfo = unescape(arr[2]);
    } else {
      accountInfo = null;
    }
    if (Boolean(accountInfo) === false) {
      return false;
    } else {
      let userName = "";
      let passWord = "";
      let Remind = "";

      let i = new Array();
      i = accountInfo.split("&");
      userName = i[0];
      passWord = i[1];
      Remind = i[2];
      this.setState({
        username: userName,
        password: passWord,
        remind: Remind
      });
    }
  };

  saveFromPath = () => {
    if (this.props.location.state != null) {
      this.setState({
        fromPath: this.props.location.state.fromPath
      });
    }
  };

  componentWillMount() {
    this.loadAccountInfo();
    this.saveFromPath();
  }

  handleSubmit = e => {
    e.preventDefault();
    let userInfo = this.props.form.getFieldsValue();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        let formData = new FormData();
        formData.append("telephone", values.username);
        formData.append("passCode", values.password);

        const init = {
          method: "POST",
          body: formData
        };
        fetch("http://localhost:8080/login", init)
          .then(res => res.json())
          .then(data => {
            console.log(data);
            if (data) {
              if (values.remember) {
                let accountInfo = "";
                if (this.state.remind === "")
                  accountInfo =
                    values.username + "&" + values.password + "&" + "true";
                else
                  accountInfo =
                    values.username +
                    "&" +
                    values.password +
                    "&" +
                    this.state.remind;
                let Days = 3;
                let exp = new Date();
                exp.setTime(exp.getTime() + Days * 24 * 60 * 60 * 1000);
                document.cookie =
                  "accountInfo" +
                  "=" +
                  escape(accountInfo) +
                  ";expires=" +
                  exp.toGMTString();
              }
              this.setState({ redirect: true });
              message.success(`${userInfo.username}, welcome`);
            } else {
              alert("Password error");
            }
          });
      }
    });
  };

  render() {
    const action = {
      type: "user",
      user: 1,
      UserName: this.state.username
    };
    if (this.state.redirect) {
      notification["success"]({
        message: "欢迎来到Travel Helper",
        duration: 1
      });
      store.dispatch(action);
      this.setState({ redirect: false });
      return <Redirect to={this.state.fromPath} />;
    }
    const { getFieldDecorator } = this.props.form;

