import React, { Component, Fragment}from "react";
import { Button,Input, Tooltip, Select, Col, Row, Menu, Dropdown, Icon,notification} from 'antd';

import { Redirect, Link } from "react-router-dom";

const InputGroup = Input.Group;
const { Option } = Select;

class RecepPage extends React.Component {
    constructor(){
        super()

        this.state={
            room:301
        }

    }

    handleSelect(value){
        this.setState({room:value});
    }

    SearchBar = () =>{
        const menu = (
            <Menu onClick={(key)=>this.OnMenu(key)}>
              <Menu.Item key="0">301</Menu.Item>
              <Menu.Item key="1">302</Menu.Item>
              <Menu.Item key="3">302</Menu.Item>
              <Menu.Item key="4">304</Menu.Item>
              <Menu.Divider />
              <Menu.Item key="5">这里是一些例子</Menu.Item>
            </Menu>
        );
        return (
            <Fragment>
                <InputGroup compact onSearch={value => console.log(value)}>
                    <div>
                    <Select defaultValue="1" onChange={(value)=>this.handleSelect(value)}>
                    <Option value="1">属性图</Option>
                    <Option value="2">关系图</Option>
                    </Select>
                    </div>

                    <Tooltip title="属性可以是任何时间,地点,景物,人">
                    <div data-step="2" data-intro="点击输入框,获取一些例子,或者自定义输入" data-position="right">
                    <Dropdown overlay={menu} trigger={['click']}>
                        <Input
                        style={{ width: 180, textAlign: 'center' }}
                        placeholder="请输入查询属性"
                        value = {this.state.from}
                        onChange={event => this.handleFrom(event)}
                        allowClear = {true}
                        />
                    </Dropdown>
                    </div>
                    </Tooltip>
                    <Button type="primary" onClick={()=>{this.handleIn()}}>搜索</Button>
                </InputGroup>
            </Fragment>
        );
    }

    render(){

        return(
            <div>
            <div style={{width:"100%", backgroundColor:"#000000"}}>
                <Row style={{height:75}}>
                <Icon style={{margin:"15px",fontSize: '35px',color:"white"}} type="menu-fold" />
                <span style={{margin:"15px",fontSize: '35px',color:"white"}}>温控计费系统</span>
                <Icon style={{margin:"15px",fontSize: '35px',color:"#87CEEB"}} type="code-sandbox" />
                <span style={{margin:"15px",fontSize: '35px',color:"white"}}>前台端</span>
                </Row>
            </div>
            <Row style={{height:500}}>
                <Col span={4}>
                <Menu
                defaultSelectedKeys={['1']}
                defaultOpenKeys={['sub1']}
                mode="inline"
                theme="dark"
                inlineCollapsed={this.state.collapsed}
                style={{height:1000}}
                >
                    <Menu.Item key="1">
                        <Icon type="pie-chart" />
                        <span style={{fontSize:17}}>账单打印</span>
                    </Menu.Item>
                    <Menu.Item key="2">
                        <Icon type="desktop" />
                        <span style={{fontSize:17}}>使用说明</span>
                    </Menu.Item>
                    <Menu.Item key="3">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>联系运营</span>
                    </Menu.Item>
                    <Menu.Item key="4">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>捐赠支持</span>
                    </Menu.Item>
                    <Menu.Item key="5">
                        <Icon type="inbox" />
                        <span style={{fontSize:17}}>退出登录</span>
                    </Menu.Item>
                </Menu>
                </Col>

                <Col>
                <Row style={{height:20}}/>
                <Col span={1}/>
                <Col span={15}>


                </Col>
                </Col>
            </Row>
            </div>
        )
    }

}

export default RecepPage;
