const defaultState = {
    user:0,
    UserName:"default",
    roomItem:{},
    roomList:[
        {
          key: '0',
          room: '101',
          temperature: 25,
          wind: '中风',
          money: 0,
          env:32
        },
        {
          key: '1',
          room: '102',
          temperature: 25,
          wind: '关机',
          money: 0,
          env:28,
        },{
          key: '2',
          room: '103',
          temperature: 25,
          wind: '关机',
          money: 0,
          env:30,
        },{
          key: '3',
          room: '104',
          temperature: 25,
          wind: '关机',
          money: 0,
          env:29
        },{
          key: '4',
          room: '105',
          temperature: 25,
          wind: '关机',
          money: 0,
          env:35
        },
    ],
    lowTem:18,
    highTem:25,
}
export default (state = defaultState, action) => {
    if(action.type === "user"){
        const newState = JSON.parse(JSON.stringify(state));
        newState.user = action.user;
        newState.UserName = action.UserName;
        return newState;
    }
    if(action.type === "RoomPage"){
        const newState = JSON.parse(JSON.stringify(state));
        newState.roomItem = action.roomItem;
        return newState;
    }
    return state;
}
